#funtion to sort mutation type to synonymous, non-synonymous or indel
def determine_mut_kind(row):
    if pd.notna(row["protein"]):
        if pd.notna(row['mutation_type']):
            return "Not-Syn"
        elif ("-" in row["mutation"]) or ("+" in row["mutation"]):
            return "Indel"
        else:
            return "Syn" 
    elif ("-" in row["mutation"]) or ("+" in row["mutation"]):
        return "Indel"
    elif (~pd.notna(row['mutation_type'])):
        return "Syn"

#add relevant missing timepoint for all mutations
def add_missing_timepoints(df, patient_col='patient', timepoint_col='days_since_first', freq_col='final_freq', mutation_col='mutation'):
    result_df = pd.DataFrame()

    # Get the unique set of relevant timepoints for each patient
    patient_timepoints = df.groupby(patient_col)[timepoint_col].unique().to_dict()

    # Loop through each patient and mutation group
    for (patient, mutation), patient_mut_data in df.groupby([patient_col, mutation_col]):
        # Get the relevant timepoints for this patient
        relevant_timepoints = patient_timepoints[patient]
        
        # Identify timepoints already present for this mutation-patient combination
        existing_timepoints = patient_mut_data[timepoint_col].unique()
        
        # Find the missing timepoints (those present in the patient's full set but missing for this mutation)
        missing_timepoints = set(relevant_timepoints) - set(existing_timepoints)
        
        # If the mutation has a frequency > 0 at any point, fill the missing timepoints
        if (patient_mut_data[freq_col] > 0).any():
            new_rows = []
            for missing_day in missing_timepoints:
                # Copy any existing row and change only the 'days_since_first' and 'final_freq'
                new_row = patient_mut_data.iloc[0].copy()
                new_row[timepoint_col] = missing_day
                new_row[freq_col] = 0
                new_rows.append(new_row)

            # Convert new rows to a DataFrame and concatenate
            new_rows_df = pd.DataFrame(new_rows)
            result_df = pd.concat([result_df, new_rows_df], ignore_index=True)

        # Add the original patient_mut_data to the result
        result_df = pd.concat([result_df, patient_mut_data], ignore_index=True)

    return result_df

def process_deletion(mutation: str, consensus_sequence: str, protein_dict: dict) -> str:
    # Check if the mutation is a deletion (contains "-")
    if "-" not in mutation:
        return mutation
    
    # Check if the number of nucleotides deleted is a multiple of 3
    deletion_part = mutation.split("-")[1]
    num_deleted_nucleotides = len(deletion_part)
    if num_deleted_nucleotides % 3 != 0:
        return mutation

    # Extract numeric part of start position and check protein
    match = re.search(r'\d+', mutation)
    if not match:
        return mutation
    start_pos = int(match.group())  # Extract the numeric part of the mutation (e.g., 21990)
    protein = None
    for prot, (start, end) in protein_dict.items():
        if start <= start_pos <= end:
            protein = prot
            break
    if not protein:
        print(f"Protein not found for mutation: {mutation}")
        return mutation
      
    # Determine what amino acids were deleted
    deleted_amino_acids = num_deleted_nucleotides // 3  # each AA is 3 nucleotides
    aa_start_pos = (start_pos - protein_dict[protein][0]) // 3 + 1  # convert nucleotide position to amino acid number
    if deleted_amino_acids == 1:
        aa_range = f"{aa_start_pos+1}"
    else:
        aa_range = f"{aa_start_pos+1}-{aa_start_pos + deleted_amino_acids}"

    # Return the formatted string
    return f"{protein}:Î”{aa_range}"

# Function to add day 0 with frequency 0 if not present, ensuring each mutation starts at day 0
def add_zero_for_day_0(df, patient):
    mutations = df['full_mut'].unique()
    complete_data = []
    # Ensure day 0 is included with frequency 0 for each mutation if not present
    for mut in mutations:
        mut_data = df[df['full_mut'] == mut]
        if 0 not in mut_data['days_since_first'].values:
            complete_data.append({
                'patient': patient,
                'days_since_first': 0,
                'final_after_manual': 0,
                'full_mut': mut,
                'mut_kind': mut_data['mut_kind'].iloc[0]  # Ensure mut_kind is taken from actual data
            })
    return pd.concat([df, pd.DataFrame(complete_data)], ignore_index=True)

# Function to remove mutations where all timepoints are 0 or NA
def remove_zero_na_mutations(df):
    filtered_df = df.groupby('full_mut').filter(
        lambda x: (x['final_after_manual'] != 0).any() and x['final_after_manual'].notna().any()
    )
    return filtered_df

# Function to remove specific timepoints for a patient
def remove_timepoints(df, patient):
    if patient in patients_to_remove:
        timepoints_to_remove = patients_to_remove[patient]
        df = df[~df['days_since_first'].isin(timepoints_to_remove)]
    return df

# Function to remove negative frequency values
def remove_negative_frequencies(df):
    return df[df['final_after_manual'] >= 0]

